import Header from './components/Header'
import Footer from './components/Footer';
import Tasks from './components/Tasks';
import { useState,useEffect} from 'react'
import AddTask from './components/AddTask';
import {render} from 'react-dom'
//import {BrowserRouter as Router,Routes,Route}  from 'react-router-dom'
import About from './components/About';
import {BrowserRouter,Switch,Route,Redirect, Link} from 'react-router-dom';


function App() {
  const [showAddTask,setShowAddTask]=useState(false)
  const [tasks, setTasks] = useState([])
  useEffect(()=>{
    const getTasks=async()=>{
      const tasksFromServer=await fetchTasks()
      setTasks(tasksFromServer)
    }
    getTasks()
  },[])

  const fetchTasks=async()=>{
    const res=await fetch('http://localhost:5000/tasks')
    const data=await res.json()
    return data
  }

  const fetchTask=async()=>{
    const res=await fetch('http://localhost:5000/tasks/${id}')
    const data=await res.json()
    return data
  }


  //Add Task
  const addTask=async(task)=>{

    const res =await fetch('http://localhost:5000/tasks',{
      method:'POST',headers:{
        'Content-type':'application/json'
      },
      body:JSON.stringify(task)
    })
    const data=await res.json()
    setTasks([...tasks,data])
    // const id=Math.floor(Math.random()*10000)+1
    // const newTask={id,...task}
    // setTasks([...tasks,newTask])
  }



  //Delete task
  const deleteTask = async (id) => {
await fetch(`http://loalhost:5000/tasks/${id}`,{method:'DELETE'})
    setTasks(tasks.filter((task) => task.id !== id))
  }


  //Toggle Reminder
  const toggleRem = async (id) => {
    const taskToToggle=await fetchTask(id)
    const updTask={...taskToToggle,reminder:!taskToToggle.reminder}

    const res =await fetch('http://localhost:5000/tasks/${id}',{
      method:'PUT',
      headers:{
        'Content-type':'application/json'
      },
      body:JSON.stringify(updTask)
    })
    const data=await res.json()

    setTasks(tasks.map((task) => task.id === id ? { ...task,reminder:!task.reminder} : task))
}
return (

  <div className="container">
    <Header onAddBtn={()=>setShowAddTask(!showAddTask)} showADD={showAddTask}/>
    <Switch>
      <Link path='/' exact render={(props)=>(
      <>{showAddTask&&<AddTask onAdd={addTask}/>}
    {tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleRem} /> : 'No Task ASsigned'}
      </>
    )}/>
      <Link path='/about' component={About}/>
   </Switch>
    <Footer/>
  </div>
);
}

export default App;
