import {FaTimes} from 'react-icons/fa'
const Task =({ask,onDelete})=>{
  return(
    <div className='task'>
      <h3>{Task.text} <FaTimes onClick={()=>onDelete(task.id)} style={{color:'red',cursor:'pointer'}}/></h3>
      <p>{Task.day}</p>
    </div>
  )
}

export default Task
